sys_script_include.script:
var GetGmailAddress = Class.create();
GetGmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


	getEmail: function() {

		gs.info('call');
		var requestedForSys_Id = this.getParameter('sysparm_user_name');
		gs.info(requestedForSys_Id);
		var glideUser = new GlideRecord('sys_user');

		glideUser.get(requestedForSys_Id);
		gs.info(glideUser.email);
		return glideUser.email;

	},

	type: 'GetGmailAddress'
});
